import React, { useState } from 'react';
import { db } from '../firebase/firebaseConfig';
import { collection, addDoc } from 'firebase/firestore';

const SurveyForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    instagram: '',
    area: '',
    frequency: [],
    shopFor: [],
    deliveryApp: '',
    favoriteStore: '',
    contact: ''
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === 'checkbox' && name === 'frequency') {
      setFormData((prev) => ({
        ...prev,
        frequency: checked
          ? [...prev.frequency, value]
          : prev.frequency.filter((item) => item !== value)
      }));
    } else if (type === 'checkbox' && name === 'shopFor') {
      setFormData((prev) => ({
        ...prev,
        shopFor: checked
          ? [...prev.shopFor, value]
          : prev.shopFor.filter((item) => item !== value)
      }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, 'surveyResponses'), formData);
      alert('Survey submitted successfully!');
      setFormData({
        name: '',
        instagram: '',
        area: '',
        frequency: [],
        shopFor: [],
        deliveryApp: '',
        favoriteStore: '',
        contact: ''
      });
    } catch (error) {
      console.error('Submission error:', error);
      alert('Something went wrong. Please try again.');
    }
  };

  return (
    <div className="container mx-auto p-4">
      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded-lg p-8">
        <h2 className="text-2xl font-bold mb-6 text-gray-800">FlashFit Survey Form</h2>

        {/* Name */}
        <div className="mb-4">
          <label className="block mb-2 text-sm font-bold text-gray-700" htmlFor="name">
            1. What is your name?
          </label>
          <input
            type="text"
            name="name"
            id="name"
            value={formData.name}
            onChange={handleChange}
            required
            className="w-full px-3 py-2 border rounded shadow appearance-none focus:outline-none focus:shadow-outline"
          />
        </div>

        {/* Instagram */}
        <div className="mb-4">
          <label className="block mb-2 text-sm font-bold text-gray-700" htmlFor="instagram">
            2. What's your Instagram handle? (optional)
          </label>
          <input
            type="text"
            name="instagram"
            id="instagram"
            value={formData.instagram}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded shadow appearance-none focus:outline-none focus:shadow-outline"
          />
        </div>

        {/* Area */}
        <div className="mb-4">
          <label className="block mb-2 text-sm font-bold text-gray-700" htmlFor="area">
            3. Which area of Hyderabad are you in?
          </label>
          <select
            name="area"
            id="area"
            value={formData.area}
            onChange={handleChange}
            required
            className="w-full px-3 py-2 border rounded shadow appearance-none focus:outline-none focus:shadow-outline"
          >
            <option value="">-- Select --</option>
            <option value="Gachibowli">Gachibowli</option>
            <option value="Kukatpally">Kukatpally</option>
            <option value="Jubilee Hills">Jubilee Hills</option>
            <option value="Madhapur">Madhapur</option>
            <option value="Other">Other</option>
          </select>
        </div>

        {/* Frequency Checkboxes */}
        <div className="mb-4">
          <label className="block mb-2 text-sm font-bold text-gray-700">
            4. How often do you buy clothes online?
          </label>
          {['once a week', 'twice a month', 'only when needed', 'rarely'].map((option) => (
            <label key={option} className="inline-flex items-center mr-4">
              <input
                type="checkbox"
                name="frequency"
                value={option}
                checked={formData.frequency.includes(option)}
                onChange={handleChange}
                className="form-checkbox h-5 w-5 text-[#f3423a] rounded"
              />
              <span className="ml-2 text-gray-700 capitalize">{option}</span>
            </label>
          ))}
        </div>

        {/* ShopFor Checkboxes */}
        <div className="mb-4">
          <label className="block mb-2 text-sm font-bold text-gray-700">
            5. What do you shop for the most?
          </label>
          {['T-shirts', 'Shirts', 'Dresses', 'Ethnic Wear', 'Bottoms'].map((item) => (
            <label key={item} className="inline-flex items-center mr-4">
              <input
                type="checkbox"
                name="shopFor"
                value={item}
                checked={formData.shopFor.includes(item)}
                onChange={handleChange}
                className="form-checkbox h-5 w-5 text-[#f3423a] rounded"
              />
              <span className="ml-2 text-gray-700">{item}</span>
            </label>
          ))}
        </div>

        {/* Delivery App */}
        <div className="mb-4">
          <label className="block mb-2 text-sm font-bold text-gray-700" htmlFor="deliveryApp">
            6. Which delivery app do you use the most?
          </label>
          <input
            type="text"
            name="deliveryApp"
            id="deliveryApp"
            value={formData.deliveryApp}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded shadow appearance-none focus:outline-none focus:shadow-outline"
          />
        </div>

        {/* Favorite Store */}
        <div className="mb-4">
          <label className="block mb-2 text-sm font-bold text-gray-700" htmlFor="favoriteStore">
            7. Favorite fashion store (online/offline)?
          </label>
          <input
            type="text"
            name="favoriteStore"
            id="favoriteStore"
            value={formData.favoriteStore}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded shadow appearance-none focus:outline-none focus:shadow-outline"
          />
        </div>

        {/* Contact */}
        <div className="mb-6">
          <label className="block mb-2 text-sm font-bold text-gray-700" htmlFor="contact">
            8. Can we reach out to you? If yes, drop your contact number.
          </label>
          <input
            type="text"
            name="contact"
            id="contact"
            value={formData.contact}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded shadow appearance-none focus:outline-none focus:shadow-outline"
          />
        </div>

        {/* Submit */}
        <div className="flex justify-center">
          <button
            type="submit"
            className="bg-[#f3423a] hover:bg-[#e2342d] text-white font-bold py-2 px-6 rounded focus:outline-none focus:shadow-outline"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default SurveyForm;
